import React, { useState } from 'react'
import { useForm } from "react-hook-form"

import Navbar from '../../../components/AdminNavbar/Index'
import Loading from '../../../components/Loading/Index'

const Create = () => {
    const { register, handleSubmit, errors } = useForm()
    const [fileErr, setFileErr] = useState(false)
    const [isLoading, setLoading] = useState(false)
    const [selectedFile, setSelectedFile] = useState(null)

    // Image onChange
    const imageChangeHandeller = event => {
        let file = event.target.files[0]
        if (file) {
            setSelectedFile(file)
        }
    }


    const onSubmit = async (data) => {
        try {
            if (!selectedFile) {
                return setFileErr(true)
            }

            let formData = new FormData()
            formData.append('name', data.name)
            formData.append('image', selectedFile)

            console.log(formData)
            // setLoading(true)

        } catch (error) {
            if (error) {
                console.log(error.response)
            }
        }
    }
    return (
        <div className="category-create">
            <Navbar back={true} title={'Create Category'} />
            {isLoading ? <Loading /> : null}

            <div className="container-fluid py-3">
                <div className="row">
                    <div className="col-12">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            {/* Name */}
                            <div className="form-group mb-4">
                                {errors.name && errors.name.message ? (
                                    <small className="text-danger">{errors.name && errors.name.message}</small>
                                ) : <small>Name</small>
                                }
                                <input
                                    type="text"
                                    name="name"
                                    className="form-control rounded-0 shadow-none"
                                    placeholder="Enter name"
                                    ref={register({
                                        required: "Name is required"
                                    })}
                                />
                            </div>

                            {/* Image */}
                            <div className="form-group mb-4">
                                {fileErr ? <small className="text-danger">Image is required</small>
                                    : <small>Image</small>
                                }
                                <br />
                                <input
                                    type="file"
                                    accept="image/*"
                                    onChange={imageChangeHandeller}
                                />
                            </div>

                            <button type="submit" style={styles.btn} className="btn btn-block shadow-none">Create</button>

                        </form>
                    </div>
                </div>
            </div>

        </div>
    );
};

export default Create;

const styles = {
    btn: {
        color: '#fff',
        fontWeight: 700,
        fontSize: 16,
        background: '#F4A261'
    }
}
