import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import url from '../../../utils/url';
import '../../../styles/user/upload-video/style.scss';

import Navbar from '../../../components/AdminNavbar/Index'
import LoadingComponent from '../../../components/Loading/Index'

const Edit = () => {
    const { id } = useParams()
    const [isLoading, setLoading] = useState(false)
    const [category, setCategory] = useState()

    useEffect(() => {
        fetchCategory()
    }, [id])

    // Fetch Category 
    const fetchCategory = async () => {
        try {
            setLoading(true)
            const response = await axios.get(`${url}admin/category/${id}`)
            if (response.status === 200) {
                setLoading(false)
                setCategory(response.data)
                console.log(response.data)
            }
        } catch (error) {
            if (error) {
                console.log(error.response)
            }
        }
    }

    return (
        <div>
            <Navbar back={true} title={'Edit Category'} />
            {isLoading ? <LoadingComponent /> : null}

            <div className="container-fluid py-3">
                <div className="row">
                    <div className="col-12">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            {/* Name */}
                            <div className="form-group mb-4">
                                {errors.name && errors.name.message ? (
                                    <small className="text-danger">{errors.name && errors.name.message}</small>
                                ) : <small>Name</small>
                                }
                                <input
                                    type="text"
                                    name="name"
                                    className="form-control rounded-0 shadow-none"
                                    placeholder="Enter name"
                                    defaultValue
                                    ref={register({
                                        required: "Name is required"
                                    })}
                                />
                            </div>

                            {/* Image */}
                            <div className="form-group mb-4">
                                {fileErr ? <small className="text-danger">Banner is required</small>
                                    : <small>Banner</small>
                                }
                                <br />
                                <input
                                    type="file"
                                    id="banner"
                                    className="inputfile"
                                    accept="image/*"
                                    onChange={imageChangeHandeller}
                                />
                                <label htmlFor="banner">
                                    <p>Banner Image</p>
                                </label>
                            </div>

                            <button
                                type="submit"
                                className="btn btn-block btn-primary rounded-0 text-white shadow-none"
                                disabled={isLoading}
                            >
                                {isLoading ? <span>Creating...</span> : <span>Create</span>}
                            </button>

                        </form>
                    </div>
                </div>
            </div>

        </div>
    );
};

export default Edit;
