export const checkIfLoggedIn = () => {
    const token = localStorage.getItem("token");
    if (token) {
        const user = token.split(' ');
        if (user.role === 'user' || 'admin') {
            return ({ role: user.role })
        }
        return false
    }
    return false;
};
