import React from 'react'
import '../../styles/auth/style.scss'
import { useForm } from "react-hook-form"
import Logo from '../../assets/static/logo.png'
import { Link } from 'react-router-dom'

const Reset = () => {
    const history = useHis
    const { register, handleSubmit, errors } = useForm()

    // Check Token
    const checkToken = (token) => {
        localStorage.setItem('token', token)
        const role = token.split('.')[0]
        if (role === 'admin') {
            return history.push('/admin')
        }

        if (role === 'user') {
            return history.push('/home')
        }
    }

    // Check if logged in 
    if (localStorage.getItem('token')) {
        checkToken(localStorage.getItem('token'))
    }

    const onSubmit = async (data) => {
        console.log(data)
    }

    return (
        <div className="auth">
            <div className="flex-center flex-column">
                <div className="card border-0">
                    <div className="card-header text-center bg-white border-0">
                        <img src={Logo} className="img-fluid" alt="..." />
                        <h4>Change Password</h4>
                    </div>
                    <div className="card-body">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            {/* Email */}
                            <div className="form-group mb-3">
                                {errors.email && errors.email.message ? (
                                    <small className="text-danger">{errors.email && errors.email.message}</small>
                                ) : <small>E-mail</small>
                                }

                                <input
                                    type="text"
                                    name="email"
                                    className="form-control shadow-none"
                                    placeholder="Your e-mail"
                                    ref={register({
                                        required: "E-mail is required",
                                        pattern: {
                                            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                            message: "Invalid email address"
                                        }
                                    })}
                                />
                            </div>

                            {/* New Password */}
                            <div className="form-group mb-3">
                                {errors.password && errors.password.message ? (
                                    <small className="text-danger">{errors.password && errors.password.message}</small>
                                ) : <small>New password</small>
                                }

                                <input
                                    type="password"
                                    name="password"
                                    className="form-control shadow-none"
                                    placeholder="*****"
                                    ref={register({
                                        required: "New password is required.",
                                    })}
                                />
                            </div>

                            <button type="submit" className="btn btn-info shadow-none text-white btn-block">Submit</button>
                            <br />

                            <div className="text-right">
                                <p>Go to ? <Link to="/">Login</Link></p>
                                <p>Have no account ? <Link to="/register">Register</Link></p>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Reset;
