import React, { useState, useEffect } from 'react'
import '../../../styles/user/upload-video/style.scss'
import Icon from 'react-icons-kit'
import { useForm } from "react-hook-form"
import { ic_cloud_upload } from 'react-icons-kit/md'
import axios from 'axios'
import url from '../../../utils/url'
import { toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'

import Navbar from '../../../components/UserNavbar/Index'

const Video = () => {
    const { register, handleSubmit, errors } = useForm()
    const [isLoading, setLoading] = useState(false)
    const [selectedBanner, setSelectedBanner] = useState(null)
    const [selectedVideo, setSelectedVideo] = useState(null)
    const [categories, setCategories] = useState([])
    const [fileErr, setFileErr] = useState({ banner: null, video: null })
    const [bannerPrev, setBannerPrev] = useState(null)
    const [videoPrev, setVideoPrev] = useState(null)

    useEffect(() => {
        // Fetch Categories
        const fetchCategories = async () => {
            try {
                const response = await axios.get(`${url}admin/category`)
                setCategories(response.data)
                setLoading(false)
            } catch (error) {
                if (error) {
                    console.log(error.response)
                    setLoading(false)
                }
            }
        }

        fetchCategories()
    }, [])

    const onSubmit = async (data) => {
        try {
            if (!selectedBanner) {
                return setFileErr({ banner: 'Banner is required' })
            }

            if (!selectedVideo) {
                return setFileErr({ video: 'Video is required' })
            }

            setLoading(true)
            let formData = new FormData()
            formData.append('user_id', localStorage.getItem('id'))
            formData.append('title', data.title)
            formData.append('category_id', data.category)
            formData.append('banner', selectedBanner)
            formData.append('video', selectedVideo)

            const response = await axios.post(`${url}admin/video`, formData)
            if (response.status === 200) {
                setLoading(false)
                toast.success(response.data.message)
            }
        } catch (error) {
            if (error) {
                setLoading(false)
                console.log(error.response)
            }
        }
    }


    return (
        <div className="upload">
            <Navbar
                title={'Upload video'}
                back={true}
            />

            <div className="container-fluid py-3">
                <div className="row">
                    <div className="col-12 px-5">
                        <div className="card border-0">
                            <div className="card-header p-3 text-center bg-white">
                                <Icon icon={ic_cloud_upload} size={40} style={{ color: '#F4A261' }} />
                                <h6 className="mb-0">Upload Your Video</h6>
                            </div>
                            <div className="card-body py-4 px-0">
                                <form onSubmit={handleSubmit(onSubmit)}>
                                    {/* Title */}
                                    <div className="form-group mb-3">
                                        {errors.title && errors.title.message ? (
                                            <small className="text-danger">{errors.title && errors.title.message}</small>
                                        ) : <small>Video title</small>
                                        }

                                        <input
                                            type="text"
                                            name="title"
                                            className="form-control shadow-none rounded-0"
                                            placeholder="Enter video title"
                                            ref={register({
                                                required: "Title is required",
                                            })}
                                        />
                                    </div>

                                    {/* Category */}
                                    <div className="form-group mb-3">
                                        {errors.category && errors.category.message ? (
                                            <small className="text-danger">{errors.category && errors.category.message}</small>
                                        ) : <small>Select category</small>
                                        }

                                        <select
                                            name="category"
                                            className="form-control shadow-none rounded-0 pl-2"
                                            ref={register({
                                                required: "Category is required",
                                            })}
                                        >
                                            <option>abc</option>
                                            <option>abc</option>
                                            <option>abc</option>
                                        </select>
                                    </div>

                                    {/* Banner Image */}
                                    <div className="form-group mb-3">
                                        <input type="file" id="image" className="inputfile" accept="image/*" />
                                        <label htmlFor="image">
                                            <p>Banner Image</p>
                                        </label>
                                    </div>

                                    {/* Video */}
                                    <div className="form-group mb-3">
                                        <input type="file" id="video" className="inputfile" accept="video/*" />
                                        <label htmlFor="video">
                                            <p>Video file</p>
                                        </label>
                                    </div>

                                    <button type="submit" className="btn btn-primary rounded-0 shadow-none text-white btn-block">Upload</button>


                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Uploading Progress */}
            {isLoading ?
                <div className="upload-progress">
                    <div className="flex-center flex-column">
                        <h6>Uploading...</h6>
                        <p>Don't leave before complete.</p>
                    </div>
                </div>
                : null}
        </div>
    );
};

export default Video;
