import React, { useState } from 'react'
import '../../styles/auth/style.scss'
import { useForm } from 'react-hook-form'
import { Link, useHistory } from 'react-router-dom'
import Axios from 'axios'
import api from '../../utils/url'
import { toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css'

import Logo from '../../assets/static/logo.png'

toast.configure()
const Login = () => {
    const history = useHistory()
    const { register, handleSubmit, errors } = useForm()
    const [isLoading, setLoading] = useState(false)

    // Check Token
    const checkToken = (token) => {
        localStorage.setItem('token', token)
        const role = token.split('.')[0]
        if (role === 'admin') {
            history.push('/admin')
        }

        if (role === 'user') {
            history.push('/home')
        }
    }

    const onSubmit = async (data) => {
        try {
            setLoading(true)
            const response = await Axios.post(`${api}login`, data)

            if (response.status === 200) {
                checkToken(response.data.token)
            }
        } catch (error) {
            if (error) {
                setLoading(false)
                toast.warn(error.response.data.message)
            }
        }
    }

    return (
        <div className="auth">
            <div className="flex-center flex-column">
                <div className="card border-0">
                    <div className="card-header text-center bg-white border-0">
                        <img src={Logo} className="img-fluid" alt="..." />
                        <h4>Login Account</h4>
                    </div>
                    <div className="card-body">
                        <form onSubmit={handleSubmit(onSubmit)}>
                            {/* Email */}
                            <div className="form-group mb-3">
                                {errors.email && errors.email.message ? (
                                    <small className="text-danger">{errors.email && errors.email.message}</small>
                                ) : <small>E-mail</small>
                                }

                                <input
                                    type="text"
                                    name="email"
                                    className="form-control shadow-none"
                                    placeholder="Your e-mail"
                                    ref={register({
                                        required: "E-mail is required",
                                        pattern: {
                                            value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                            message: "Invalid email address"
                                        }
                                    })}
                                />
                            </div>

                            {/* Password */}
                            <div className="form-group mb-3">
                                {errors.password && errors.password.message ? (
                                    <small className="text-danger">{errors.password && errors.password.message}</small>
                                ) : <small>Password</small>
                                }

                                <input
                                    type="password"
                                    name="password"
                                    className="form-control shadow-none"
                                    placeholder="*****"
                                    ref={register({
                                        required: "Please enter password",
                                    })}
                                />
                            </div>

                            <button type="submit" className="btn btn-info shadow-none text-white btn-block" disabled={isLoading}>
                                {isLoading ? <span>Logging...</span> : <span>Login</span>}
                            </button>
                            <br />

                            <div className="text-right">
                                <p>Have no account ? <Link to="/register">Register</Link></p>
                                <p>Forgot password? <Link to="/reset">Reset</Link></p>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
